import logo from './logo.svg';
import './App.css';
import {useState, useReducer } from 'react';
import { useFormStatus } from "react-dom";

function reducer(state , action){
  switch (action.type) {
    case 'increment':
      return { ...state, age: action.payload };
    case 'changeName':
      return { ...state, name: action.payload };
    default:
      return state;
}
}


function Submit() {
  const status = useFormStatus();
  return <button disabled={status.pending}>Submit</button>
}

function App() {

  const [on, setOn] = useState(false);
  const [state, dispatch] = useReducer(reducer, { age: 42 , name:'reactapp'});

  return (
    <div className="App">
      {/* {on ? <div> true </div>: <div>false</div>} */}
      {on && <div>true</div> }
      {on && 'jsobj'}
      <div>{state.age}, {state.name}</div>
      <form>
        <input type='text' onChange={(e)=>dispatch({type:'increment', payload:e.target.value})}/>
        <input type='text' onChange={(e)=>dispatch({type:'changeName', payload:e.target.value})}/>
        <Submit />
      </form>
    </div>
  );
}

export default App;
