import React, { useState, useEffect } from 'react';

const Events = () => {
    const [events, setEvents] = useState([]);
    const [newEvent, setNewEvent] = useState({ title: '', description: '' });
    const [editEvent, setEditEvent] = useState(null);

    // Fetch events
    useEffect(() => {
        fetch('http://localhost:8000/api/events/')
            .then(response => response.json())
            .then(data => setEvents(data))
            .catch(error => console.error('Error fetching events:', error));
    }, []);

    // Handle input change for the create event form
    const handleChange = (e) => {
        setNewEvent({ ...newEvent, [e.target.name]: e.target.value });
    };

    // Create a new event
    const handleCreate = (e) => {
        e.preventDefault();
        fetch('http://localhost:8000/api/events/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newEvent),
        })
            .then(response => response.json())
            .then(data => {
                setEvents([...events, data]);
                setNewEvent({ title: '', description: '' });  // Clear form
            })
            .catch(error => console.error('Error creating event:', error));
    };

    // Delete an event
    const handleDelete = (id) => {
        fetch(`http://localhost:8000/api/events/${id}/`, {
            method: 'DELETE',
        })
            .then(() => {
                setEvents(events.filter(event => event.id !== id));
            })
            .catch(error => console.error('Error deleting event:', error));
    };

    // Handle input change for the edit event form
    const handleEditChange = (e) => {
        setEditEvent({ ...editEvent, [e.target.name]: e.target.value });
    };

    // Edit an event
    const handleEdit = (id) => {
        fetch(`http://localhost:8000/api/events/${id}/`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(editEvent),
        })
            .then(response => response.json())
            .then(data => {
                setEvents(events.map(event => (event.id === id ? data : event)));
                setEditEvent(null);  // Clear edit form
            })
            .catch(error => console.error('Error editing event:', error));
    };

    return (
        <div>
            <h1>Events</h1>

            {/* Create event form */}
            <form onSubmit={handleCreate}>
                <input
                    type="text"
                    name="title"
                    placeholder="Event Title"
                    value={newEvent.title}
                    onChange={handleChange}
                    required
                />
                <textarea
                    name="description"
                    placeholder="Event Description"
                    value={newEvent.description}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Create Event</button>
            </form>

            <ul>
                {events.map(event => (
                    <li key={event.id}>
                        {editEvent && editEvent.id === event.id ? (
                            // Edit event form
                            <form onSubmit={() => handleEdit(event.id)}>
                                <input
                                    type="text"
                                    name="title"
                                    value={editEvent.title}
                                    onChange={handleEditChange}
                                />
                                <textarea
                                    name="description"
                                    value={editEvent.description}
                                    onChange={handleEditChange}
                                />
                                <button type="submit">Save</button>
                            </form>
                        ) : (
                            <>
                                <a href={`/events/${event.id}`}>{event.title}</a>
                                <button onClick={() => setEditEvent(event)}>Edit</button>
                                <button onClick={() => handleDelete(event.id)}>Delete</button>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Events;
