import logo from './logo.svg';
import './App.css';
import {useState, useReducer } from 'react';
import Parent from './parent';
import MyContext from './context';
function reducer(state , action){
  switch (action.type) {
    case 'increment':
      return { ...state, age: action.payload };
    case 'changeName':
      return { ...state, name: action.payload };
    default:
      return state;
}
}


function App() {

  const [on, setOn] = useState(false);
  const [state, dispatch] = useReducer(reducer, { age: 42 , name:'reactapp'});
  const [contextState, setContextState] = useState('initial Context')
  return (
    <div className="App">
      <MyContext.Provider value={{contextState, setContextState}}>
      <h1>first Context value : {contextState} </h1>

      {/* {on ? <div> true </div>: <div>false</div>} */}
      {on && <div>true</div> }
      {on && 'jsobj'}
      <div>{state.age}, {state.name}</div>
      <form>
        <input type='text' onChange={(e)=>dispatch({type:'increment', payload:e.target.value})}/>
        <input type='text' onChange={(e)=>dispatch({type:'changeName', payload:e.target.value})}/>
      </form>
      <Parent/>
  </MyContext.Provider>
    </div>
  );
}

export default App;
